/**
 * Represents an asynchronous task queue used for meta patches
 * @private
 */
declare class AsyncQueue {
    /**
     * The queued tasks
     */
    private tasks;
    constructor();
    /**
     * The amount of queued tasks
     */
    get length(): number;
    /**
     * Adds a task to the queue
     */
    wait(): Promise<void>;
    /**
     * Resolves the first task from the queue
     */
    shift(): void;
}
export default AsyncQueue;
